#!/usr/bin/python# coding: utf-8import h5pyimport globimport osimport redirs_apps = [x for x in glob.glob("./*") if os.path.isdir(x)]dict_apps = {}#get app namefor dir_name in dirs_apps:    dict_apps[dir_name[2:]] = dir_nameprint(dict_apps)apps_table = {"alps":"ALPS","xtapp":"xTAPP", "komega":"Kω",              "espresso":"QUANTUM ESPRESSO", "openmx": "OpenMX",              "modylas": "MODYLAS", "gromacs":"Gromacs",              "lammps":"LAMMPS", "dsqss":"DSQSS",              "mvmc":"mVMC", "tapioca":"TAPIOCA",              "xcrysden":"XCRYSDEN",               "dcore":"DCore", "respack":"RESPACK",              "tenes":"TeNeS" }#apps_table = {"hphi": "HΦ"}list_read = ["name", "summary", "license", "official_url", "url_to_ma"]with h5py.File("materiapps_info_ja.h5", "r") as fr:    for key, value in apps_table.items():        keys = fr[value].keys()        with open(os.path.join(dict_apps[key], "README_ja.md"), "w") as fw:            for item in list_read:                if fr[value].get(item) is None:                    continue                if item == "name":                    fw.write("# {} \n\n".format(fr[value].get(item)[()]))                elif item == "url_to_ma":                    fw.write("## {} \n\n {}\n\n".format("MateriApps URL", fr[value].get(item)[()]))                elif item == "official_url":                    fw.write("## {} \n\n {}\n\n".format("OFFICIAL PAGE", fr[value].get(item)[()]))                else:                    fw.write("## {} \n\n {}\n\n".format(item.upper(), fr[value].get(item)[()]))